var DISPLAY_ANSWER=$("<div></div>",{text:"Show Answer",id:"nextButton"});$(document).ready(function(){$("input[type=submit]").hide();var b=$(".testcard");b.hide();b.eq(0).show();beginTestCard(b,0)});function beginTestCard(f,e){var h=f.eq(e),g=h.find(".back");g.hide().before(DISPLAY_ANSWER);DISPLAY_ANSWER.click(function(){DISPLAY_ANSWER.remove();g.show()});h.show().find("input[type=radio]").click(function(){f.length==e+1?$("form").submit():(h.hide(),beginTestCard(f,e+1))})};